class PDFMaker extends CRMEntity {
	var $log;
	var $db;
	
	function PDFMaker() {
		$this->log =LoggerManager::getLogger('account');
		$this->db = PearDatabase::getInstance();
	}
	
	function vtlib_handler($modulename, $event_type) 
	{
		if($event_type == 'module.postinstall') 
		{
			$this->executeSql();
		}
	}
	
    function executeSql()
	{
		global $adb,$vtiger_current_version;
		
		if($adb->num_rows($adb->query("SELECT id FROM vtiger_pdfmaker_seq"))<1)
		{
		$adb->query("INSERT INTO vtiger_pdfmaker_seq VALUES('0')");
		}
		
		$productblocTbl="CREATE TABLE IF NOT EXISTS `vtiger_pdfmaker_productbloc_tpl` (
		                `id` int(11) NOT NULL auto_increment,
		                `name` varchar(255) collate utf8_bin NOT NULL,
		                `body` longtext collate utf8_bin NOT NULL,
		                PRIMARY KEY  (`id`)
		              ) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=2";
		$results["create table vtiger_pdfmaker_productbloc_tpl"]=$adb->query($productblocTbl);
		
		$productblocData="INSERT INTO `vtiger_pdfmaker_productbloc_tpl` (`id`, `name`, `body`) VALUES
		              (1, 'product block for individual tax', ),
		              (2, 'product block for group tax', )";
		
		$adb->query($productblocData);
		
		$picklistTbl="CREATE TABLE IF NOT EXISTS `vtiger_pdfmaker_ignorepicklistvalues` (
		            `value` varchar(100) collate utf8_bin NOT NULL,
		            PRIMARY KEY  (`value`)
		          ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_bin";
		$results["create table vtiger_pdfmaker_ignorepicklistvalues"]=$adb->query($picklistTbl);
		
		$pdfmakerData="INSERT INTO `vtiger_pdfmaker` (`templateid`, `filename`, `module`, `body`, `description`, `deleted`) VALUES
		            (1, 'Invoice', 'Invoice', , 'Template for Invoice', 0),
					(2, 'SalesOrder', 'SalesOrder', , 'Template for SalesOrder', 0),
					(3, 'PurchaseOrder', 'PurchaseOrder', , 'Template for PurchaseOrder', 0),
					(4, 'Quotes', 'Quotes', , 'Templates for Quotes', 0)";
		
		$adb->query($pdfmakerData);
		
		$settingsData="INSERT INTO `vtiger_pdfmaker_settings` (`templateid`, `margin_top`, `margin_bottom`, `margin_left`, `margin_right`, `format`, `orientation`, `decimals`, `decimal_point`, `thousands_separator`, `header`, `footer`) VALUES
		            (2, 2.0, 2.0, 2.0, 2.0, 'A4', 'portrait', 2, ',', '', '<p>\r\n	##PAGE##/##PAGES##</p>\r\n', '<p style=\"text-align: center;\">\r\n	<span style=\"font-size:10px;\">$"."COMPANY_NAME"."$ <small>&bull; </small>$"."COMPANY_ADDRESS"."$ <small>&bull; </small> $"."COMPANY_ZIP"."$<small> </small>$"."COMPANY_CITY"."$<small> &bull; </small>$"."COMPANY_STATE"."$</span></p>\r\n'),
					(1, 2.0, 2.0, 2.0, 2.0, 'A4', 'portrait', 2, ',', '', '<p>\r\n	##PAGE##/##PAGES##</p>\r\n', '<p style=\"text-align: center;\">\r\n	<span style=\"font-size:10px;\">$"."COMPANY_NAME"."$ <small>&bull; </small>$"."COMPANY_ADDRESS"."$ <small>&bull; </small> $"."COMPANY_ZIP"."$<small> </small>$"."COMPANY_CITY"."$<small> &bull; </small>$"."COMPANY_STATE"."$</span></p>\r\n'),
					(3, 2.0, 2.0, 2.0, 2.0, 'A4', 'portrait', 2, ',', '', '<p>\r\n	##PAGE##/##PAGES##</p>\r\n', '<p style=\"text-align: center;\">\r\n	<span style=\"font-size:10px;\">$"."COMPANY_NAME"."$ <small>&bull; </small>$"."COMPANY_ADDRESS"."$ <small>&bull; </small> $"."COMPANY_ZIP"."$<small> </small>$"."COMPANY_CITY"."$<small> &bull; </small>$"."COMPANY_STATE"."$</span></p>\r\n'),
					(4, 2.0, 2.0, 2.0, 2.0, 'A4', 'portrait', 2, ',', '', '<p>\r\n	##PAGE##/##PAGES##</p>\r\n', '<p style=\"text-align: center;\">\r\n	<span style=\"font-size:10px;\">$"."COMPANY_NAME"."$ <small>&bull; </small>$"."COMPANY_ADDRESS"."$ <small>&bull; </small> $"."COMPANY_ZIP"."$<small> </small>$"."COMPANY_CITY"."$<small> &bull; </small>$"."COMPANY_STATE"."$</span></p>\r\n')";
		$adb->query($settingsData);
		
		$seqUpdate="UPDATE vtiger_pdfmaker_seq SET id='8' WHERE id='0'";
		$adb->query($seqUpdate);
		
		$tabid = getTabId("PDFMaker");  
		$adb->query("INSERT INTO vtiger_profile2standardpermissions SELECT profileid, ".$tabid.", 0, 0 FROM vtiger_profile");
		$adb->query("INSERT INTO vtiger_profile2standardpermissions SELECT profileid, ".$tabid.", 1, 0 FROM vtiger_profile");
		$adb->query("INSERT INTO vtiger_profile2standardpermissions SELECT profileid, ".$tabid.", 2, 0 FROM vtiger_profile");
		$adb->query("INSERT INTO vtiger_profile2standardpermissions SELECT profileid, ".$tabid.", 3, 0 FROM vtiger_profile");
		$adb->query("INSERT INTO vtiger_profile2standardpermissions SELECT profileid, ".$tabid.", 4, 0 FROM vtiger_profile");
		
		$linksid = $adb->getOne("SELECT (id + 1) as newid FROM vtiger_links_seq",0,"newid");
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 20, 'DETAILVIEWWIDGET', 'PDFMaker', 'module=PDFMaker&action=PDFMakerAjax&file=getPDFActions&record=$"."RECORD"."$', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 21, 'DETAILVIEWWIDGET', 'PDFMaker', 'module=PDFMaker&action=PDFMakerAjax&file=getPDFActions&record=$"."RECORD"."$', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 22, 'DETAILVIEWWIDGET', 'PDFMaker', 'module=PDFMaker&action=PDFMakerAjax&file=getPDFActions&record=$"."RECORD"."$', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 23, 'DETAILVIEWWIDGET', 'PDFMaker', 'module=PDFMaker&action=PDFMakerAjax&file=getPDFActions&record=$"."RECORD"."$', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 20, 'HEADERSCRIPT', 'PDFMakerJS', 'modules/PDFMaker/PDFMakerActions.js', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 21, 'HEADERSCRIPT', 'PDFMakerJS', 'modules/PDFMaker/PDFMakerActions.js', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 22, 'HEADERSCRIPT', 'PDFMakerJS', 'modules/PDFMaker/PDFMakerActions.js', NULL, 1)");
		
		$linksid++;
		$adb->query("INSERT INTO vtiger_links (linkid,tabid,linktype,linklabel,linkurl,linkicon,sequence) VALUES (".$linksid.", 23, 'HEADERSCRIPT', 'PDFMakerJS', 'modules/PDFMaker/PDFMakerActions.js', NULL, 1)");
		
		$seqUpdate="UPDATE vtiger_links_seq SET id='".$linksid."'";
		$adb->query($seqUpdate);
		
		include_once("version.php");  
		$adb->query("INSERT INTO vtiger_pdfmaker_releases (version, date, updated) VALUES('".$version."', NOW(), 1)");
	}
}
?>
